mapper.select:
SELECT

FROM ser_course a

WHERE a.id = #{id}

		SELECT

		FROM ser_course a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.code like '%' #{code} '%'


				AND coach.nameCn like '%' #{coachName} '%'


				AND a.beginDate BETWEEN #{beginTime} and #{endTime}
 				AND a.endDate BETWEEN #{beginTime} and #{endTime}


				AND a.strInWeek = #{strInWeek}


				AND a.courseAddress = #{courseAddress}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT

FROM ser_course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_course SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
