mapper.select:
SELECT

FROM ser_course_details a

WHERE a.id = #{id}

SELECT

FROM ser_course_details a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM ser_course_details a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_course_details SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
