mapper.select:
SELECT

FROM ser_sale a

WHERE a.id = #{id}

SELECT
	concat(sbt.nameCn,"(",sbt.nameEn,")") as studentName,

FROM ser_sale a
left join sys_base_student sbt on sbt.code = a.studentCode and sbt.del_flag = 0


	a.del_flag = #{DEL_FLAG_NORMAL}


		and a.code like concat('%',#{code},'%')



		and a.studentCode like concat('%',#{studentCode},'%')



		and a.paid_flag = #{paidFlag}


		and a.memberFee_flag = #{memberFeeFlag}


		and a.group_flag = #{groupFlag}





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT concat(sbt.nameCn,"(",sbt.nameEn,")") as studentName,
FROM ser_sale a left join sys_base_student sbt on sbt.code = a.studentCode and sbt.del_flag = 0
a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_sale SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select count(1)
FROM ser_sale a
where a.create_date BETWEEN #{beginTime} and #{endTime}

mapper.update:
UPDATE ser_sale SET
	group_flag = #{status}
WHERE id = #{saleId}
