mapper.select:
SELECT 

FROM sys_base_coach a

WHERE a.id = #{id}

SELECT 

FROM sys_base_coach a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.nameCn = #{nameCn}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_base_coach a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_base_coach SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
