mapper.select:
SELECT * FROM sys_dict WHERE del_flag = #{DEL_FLAG_NORMAL}
AND label LIKE '%'||#{label}||'%' CONCAT('%', #{label}, '%')
AND type = #{type}
AND description LIKE '%'||#{description}||'%' CONCAT('%', #{description}, '%')
ORDER BY ${page.orderBy}
ORDER BY type, sort, update_date DESC

mapper.update:
UPDATE sys_dict SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE sys_dict SET
	status = #{status}
WHERE id = #{id}
